import os
import sys
import ctypes
import time
import random
import tempfile
import base64
import requests
import pygame
import atexit
import shutil

# --- CẤU HÌNH ---
WIN_WIDTH, WIN_HEIGHT = 400, 400
FALL_WAIT = 3_000
EXIT_WAIT = 2.5
GRAVITY = 2000.0
SHAKE_DURATION = 1.5
SHAKE_MAG = 250

# 2 ảnh riêng biệt: ảnh nền và icon
background_img = {"type": "url", "data": "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcRpemPTnDyYh9QUIiff6_ukvJTR2vwTepE3Rw&s"}

icon_img = {"type": "base64", "data": (
    ""
)}

sound_url = "https://www.myinstants.com/media/sounds/jixaw-metal-pipe-falling-sound.mp3"

# Thư mục tạm
tmpdir = os.path.join(tempfile.gettempdir(), "Ica_legacy")
os.makedirs(tmpdir, exist_ok=True)

def save_image(item, filename):
    path = os.path.join(tmpdir, filename)
    if item["type"] == "url":
        r = requests.get(item["data"])
        r.raise_for_status()
        with open(path, "wb") as f:
            f.write(r.content)
    else:
        data = base64.b64decode(item["data"])
        with open(path, "wb") as f:
            f.write(data)
    return path

def download_sound(url):
    path = os.path.join(tmpdir, "impact.mp3")
    if not os.path.exists(path):
        r = requests.get(url)
        r.raise_for_status()
        with open(path, "wb") as f:
            f.write(r.content)
    return path

def get_screen_size():
    user32 = ctypes.windll.user32
    return user32.GetSystemMetrics(0), user32.GetSystemMetrics(1)

def move_window(hwnd, x, y):
    ctypes.windll.user32.SetWindowPos(hwnd, 0, int(x), int(y), 0, 0, 0x5)

def clean_temp():
    try:
        shutil.rmtree(tmpdir, ignore_errors=True)
    except Exception as e:
        print(f"Lỗi khi dọn tệp tạm: {e}")

atexit.register(clean_temp)

# Lưu 2 ảnh riêng
bg_path = save_image(background_img, "background.png")
icon_path = save_image(icon_img, "icon.png")
sound_path = download_sound(sound_url)

# Khởi tạo Pygame
pygame.init()
pygame.mixer.init()
screen = pygame.display.set_mode((WIN_WIDTH, WIN_HEIGHT))
pygame.display.set_caption("Ica")

# Đặt icon cửa sổ
icon_surf = pygame.image.load(icon_path)
pygame.display.set_icon(icon_surf)

hwnd = pygame.display.get_wm_info()['window']

sw, sh = get_screen_size()
x0 = (sw - WIN_WIDTH) / 2
y0 = (sh - WIN_HEIGHT) / 2
move_window(hwnd, x0, y0)

bg_img = pygame.image.load(bg_path)
bg_img = pygame.transform.scale(bg_img, (WIN_WIDTH, WIN_HEIGHT))

impact = pygame.mixer.Sound(sound_path)

screen.blit(bg_img, (0,0))
pygame.display.flip()

pygame.time.delay(FALL_WAIT)

y = y0
v = 0.0
y_target = sh - WIN_HEIGHT

clock = pygame.time.Clock()
running = True
while running:
    dt = clock.tick(60) / 1000
    v += GRAVITY * dt
    y += v * dt
    if y >= y_target:
        y = y_target
        running = False
    move_window(hwnd, x0, y)
    for ev in pygame.event.get():
        if ev.type == pygame.QUIT:
            pygame.quit()
            sys.exit()

impact.play()
shake_t0 = time.time()
while time.time() - shake_t0 < SHAKE_DURATION:
    offset_x = random.uniform(-SHAKE_MAG, SHAKE_MAG)
    offset_y = random.uniform(-SHAKE_MAG, SHAKE_MAG)
    move_window(hwnd, x0 + offset_x, y + offset_y)
    pygame.time.delay(16)

move_window(hwnd, x0, y)

time.sleep(EXIT_WAIT)
pygame.quit()
sys.exit()
